/* icon inline */
.text-block.icon-inline .foreground > div {
  display: grid;
  column-gap: var(--spacing-xs);
  align-items: center;
  grid-template-columns: auto 1fr;
  justify-content: start;
}

.text-block.icon-inline .foreground [class^="body-"],
.text-block.icon-inline .foreground .cta-container {
  grid-column: span 2;
  margin-top: 0;
}

.text-block.icon-inline .icon-area {
  margin-bottom: var(--spacing-xs);
}

.text-block.icon-inline .icon-area picture img {
  min-width: var(--icon-size-m);
  max-width: var(--icon-size-m);
}

.text-block.center .foreground > .mobile-up {
  justify-content: center;
}

.text-block.right .foreground > .mobile-up {
  justify-content: end;
}

.text-block .foreground > .desktop-up,
.text-block .foreground > .tablet-up,
.text-block.icon-inline .foreground > .desktop-up,
.text-block.icon-inline .foreground > .tablet-up {
  display: none;
}

/* Text Alignment */
.section[class*="-up"] .text-block:not(.legal, .link-farm) {
  display: flex;
  align-self: stretch;
}

.section[class*="-up"] .text-block:not(.legal, .link-farm) .foreground {
  display: flex;
  align-self: stretch;
  width: 100%;
}

.section[class*="-up"] .text-block:not(.legal, .link-farm) .foreground > div {
  display: flex;
  flex-direction: column;
  width: 100%;
}

.section[class*="-up"] .text-block:not(.legal, .link-farm) [class^="heading"],
.section[class*="-up"] .text-block:not(.legal, .link-farm) p:not([class^="detail-"]),
.section[class*="-up"] .text-block:not(.legal, .link-farm) [class^="body-"] {
  margin-bottom: 0;
}

.section[class*="-up"] .text-block:not(.legal, .link-farm) .cta-container {
  display: flex;
  align-items: flex-end;
  width: 100%;
  height: 100%;
}

/* Tablet up */
@media screen and (min-width: 600px) {
  .inset.text-block .foreground::before {
    display: none;
  }

  .inset.text-block .foreground {
    border-left: 2px solid var(--color-gray-400);
  }

  .inset.text-block .foreground > div {
    padding-left: var(--spacing-l);
  }

  .text-block .foreground > .tablet-up,
  .text-block .foreground > .tablet-up.desktop-up {
    display: block;
  }

  .text-block.icon-inline .foreground > .tablet-up,
  .text-block.icon-inline .foreground > .tablet-up.desktop-up {
    display: grid;
  }

  .text-block.center .foreground > .tablet-up {
    justify-content: center;
  }

  .text-block.right .foreground > .tablet-up {
    justify-content: end;
  }

  .text-block .foreground > .mobile-up,
  .text-block .foreground > .desktop-up,
  .text-block.icon-inline .foreground > .mobile-up,
  .text-block.icon-inline .foreground > .desktop-up {
    display: none;
  }
}

/* Desktop up */
@media screen and (min-width: 1200px) {
  .text-block .foreground > .desktop-up,
  .text-block .foreground > .tablet-up.desktop-up {
    display: block;
  }

  .text-block.icon-inline .foreground > .desktop-up,
  .text-block.icon-inline .foreground > .tablet-up.desktop-up {
    display: grid;
  }

  .text-block.center .foreground > .desktop-up {
    justify-content: center;
  }

  .text-block.right .foreground > .desktop-up {
    justify-content: end;
  }

  .text-block .foreground > .mobile-up,
  .text-block .foreground > .tablet-up,
  .text-block.icon-inline .foreground > .mobile-up,
  .text-block.icon-inline .foreground > .tablet-up {
    display: none;
  }
}

import { decorateBlockBg, decorateBlockText, getBlockSize, decorateTextOverrides } from '../../utils/decorate.js';
import { createTag, loadStyle, getConfig, loadBlock } from '../../utils/utils.js';

// size: [heading, body, ...detail]
const blockTypeSizes = {
  standard: {
    small: ['s', 's', 's'],
    medium: ['m', 'm', 'm'],
    large: ['l', 'l', 'l'],
    xlarge: ['xl', 'xl', 'xl'],
  },
  inset: {
    small: ['s', 'm'],
    medium: ['m', 'l'],
    large: ['l', 'xl'],
    xlarge: ['xl', 'xxl'],
  },
  text: {
    xxsmall: ['xxs', 'xxs'],
    small: ['m', 's', 's'],
    medium: ['l', 'm', 'm'],
    large: ['xl', 'm', 'l'],
    xlarge: ['xxl', 'l', 'xl'],
  },
};

function decorateMultiViewport(foreground) {
  const viewports = ['mobile-up', 'tablet-up', 'desktop-up'];
  if (foreground.childElementCount === 2 || foreground.childElementCount === 3) {
    [...foreground.children].forEach((child, index) => {
      child.className = viewports[index];
      if (foreground.childElementCount === 2 && index === 1) child.className = 'tablet-up desktop-up';
    });
  }
  return foreground;
}

function decorateBlockIconArea(content, el) {
  const first = content.children[0];
  if (first?.querySelector('img')) {
    const areaClass = el.className.match(/-(lockup|icon)/);
    first.classList.add(areaClass ? `${areaClass[1]}-area` : 'image');
  }
  const headings = content.querySelectorAll('h1, h2, h3, h4, h5, h6');
  if (!headings) return;
  headings.forEach((h) => {
    const hPrevElem = h.previousElementSibling;
    if (hPrevElem?.childElementCount) {
      const picCount = [...hPrevElem.children].reduce((result, item) => {
        let count = result;
        if (item.nodeName === 'PICTURE') count += 1;
        return count;
      }, 0);
      if (picCount === hPrevElem.childElementCount) hPrevElem.classList.add('icon-area');
    }
  });
}

function decorateLinkFarms(el) {
  const { miloLibs, codeRoot } = getConfig();
  loadStyle(`${miloLibs || codeRoot}/blocks/text/link-farms.css`);
  const [title, foregroundDiv] = [...el.querySelectorAll('.foreground')];
  const hCount = foregroundDiv.querySelectorAll('h1, h2, h3, h4, h5, h6').length;
  title.querySelector('h1, h2, h3, h4, h5, h6')?.classList.add('heading-l');
  foregroundDiv.querySelectorAll('p').forEach((p) => p.classList.add('body-s'));
  foregroundDiv.querySelectorAll('div').forEach((divElem, index) => {
    const heading = divElem.querySelector('h1, h2, h3, h4, h5, h6');
    heading?.classList.add('heading-xs');
    if (!hCount) return;
    if (!heading) {
      divElem.prepend(createTag('h3', { class: 'no-heading heading-xs' }));
      return;
    }
    const sibling = index % 2 === 0
      ? divElem.nextElementSibling
      : divElem.previousElementSibling;
    sibling?.classList.add('hspace');
    if (index > 0) divElem.classList.add('has-heading');
    if (index > 1) foregroundDiv.classList.add('gap-xl');
  });
}

function addStyle(filename) {
  const { miloLibs, codeRoot } = getConfig();
  const base = miloLibs || codeRoot;
  loadStyle(`${base}/styles/${filename}.css`);
}

export default async function init(el) {
  el.classList.add('text-block', 'con-block');
  let rows = el.querySelectorAll(':scope > div');
  if (rows.length > 1 || el.matches('.accent-bar')) {
    if (rows[0].textContent !== '') el.classList.add('has-bg');
    const [head, ...tail] = rows;
    decorateBlockBg(el, head);
    rows = tail || rows;
  }
  const helperClasses = [];
  let blockType = 'text';
  const size = el.classList.contains('legal') ? 'xxsmall' : getBlockSize(el);
  ['inset', 'long-form', 'bio'].forEach((variant, index) => {
    if (el.classList.contains(variant)) {
      helperClasses.push('max-width-8-desktop');
      blockType = (index > 0) ? 'standard' : variant;
    }
  });
  const hasLinkFarm = el.classList.contains('link-farm');
  rows.forEach((row) => {
    row.classList.add('foreground');
    if (!hasLinkFarm) {
      [...row.children].forEach((c) => decorateBlockText(c, blockTypeSizes[blockType][size]));
      decorateMultiViewport(row);
    }
    [...row.children].forEach((c) => decorateBlockIconArea(c, el));
  });
  if (el.classList.contains('full-width')) helperClasses.push('max-width-8-desktop', 'center', 'xxl-spacing');
  if (el.classList.contains('intro')) helperClasses.push('max-width-8-desktop', 'xxl-spacing-top', 'xl-spacing-bottom');
  if (el.classList.contains('vertical')) {
    const elAction = el.querySelector('.action-area');
    if (elAction) elAction.classList.add('body-s');
  }
  if (hasLinkFarm) decorateLinkFarms(el);
  el.classList.add(...helperClasses);
  decorateTextOverrides(el);

  const lastActionArea = el.querySelector('.action-area:last-of-type');
  if (lastActionArea) {
    const div = createTag('div', { class: 'cta-container' });
    lastActionArea.insertAdjacentElement('afterend', div);
    div.append(lastActionArea);
  }

  const mnemonicList = el.querySelector('.mnemonic-list');
  const foreground = mnemonicList?.closest('.foreground');
  if (foreground) {
    mnemonicList.querySelectorAll('p').forEach((product) => product.removeAttribute('class'));
    await loadBlock(mnemonicList);
  }
  if (el.matches('[class*="rounded-corners"]')) addStyle('rounded-corners');
  if (el.matches('[class*="-lockup"]')) addStyle('iconography');
  // Override Detail with Title L style if class exists - Temporary solution until Spectrum 2
  if (el.classList.contains('l-title')) {
    el.querySelectorAll('[class*="detail-"]')?.forEach((detail) => detail.classList.add('title-l'));
  }
}

import { getMetadata, getConfig, getFederatedUrl } from '../../../../utils/utils.js';
import { toFragment, lanaLog } from '../../utilities/utilities.js';

const metadata = {
  seo: 'breadcrumbs-seo',
  seoLegacy: 'breadcrumb-seo',
  fromFile: 'breadcrumbs-from-file',
  showCurrent: 'breadcrumbs-show-current-page',
  hiddenEntries: 'breadcrumbs-hidden-entries',
  pageTitle: 'breadcrumbs-page-title',
  base: 'breadcrumbs-base',
  fromUrl: 'breadcrumbs-from-url',
};

const setBreadcrumbSEO = (breadcrumbs) => {
  const seoDisabled = (getMetadata(metadata.seo) || getMetadata(metadata.seoLegacy)) === 'off';
  if (seoDisabled || !breadcrumbs) return;
  const breadcrumbsSEO = {
    '@context': 'https://schema.org',
    '@type': 'BreadcrumbList',
    itemListElement: [],
  };
  breadcrumbs.querySelectorAll('ul > li').forEach((item, idx) => {
    const link = item.querySelector('a');
    const name = link ? link.innerText.trim() : [...item.childNodes].filter((node) => !node.matches?.('span[aria-hidden="true"]')).map((node) => node.textContent.trim()).join('');
    breadcrumbsSEO.itemListElement.push({
      '@type': 'ListItem',
      position: idx + 1,
      name,
      item: link?.href,
    });
  });
  const script = toFragment`<script type="application/ld+json">${JSON.stringify(
    breadcrumbsSEO,
  )}</script>`;
  document.head.append(script);
};

const createBreadcrumbs = (element) => {
  if (!element) return null;
  const ul = element.querySelector('ul');
  const pageTitle = getMetadata(metadata.pageTitle);
  if (pageTitle || getMetadata(metadata.showCurrent) === 'on') {
    ul.append(toFragment`
      <li>
        ${pageTitle || document.title}
      </li>
    `);
  }

  const hiddenEntries = getMetadata(metadata.hiddenEntries)
    ?.toLowerCase()
    .split(',')
    .map((item) => item.trim()) || [];

  ul.querySelectorAll('li').forEach((li, index) => {
    if (hiddenEntries.includes(li.innerText?.toLowerCase().trim())) li.remove();
    if (index > 0) li.insertAdjacentHTML('afterbegin', '<span aria-hidden="true">/</span>');
  });

  const noTransform = element.classList.contains('no-transform') ? ' no-transform' : '';

  const breadcrumbs = toFragment`
    <div class="feds-breadcrumbs-wrapper">
      <nav class="feds-breadcrumbs${noTransform}" aria-label="Breadcrumb">${ul}</nav>
    </div>
  `;
  ul.querySelector('li:last-of-type')?.setAttribute('aria-current', 'page');
  return breadcrumbs;
};

const createWithBase = async (el) => {
  const element = el || toFragment`<div><ul></ul></div>`;
  const url = getFederatedUrl(getMetadata(metadata.base));
  if (!url) return null;
  try {
    const resp = await fetch(`${url}.plain.html`);
    const text = await resp.text();
    const base = new DOMParser().parseFromString(text, 'text/html').body;
    element.querySelector('ul')?.prepend(...base.querySelectorAll('li'));
    return createBreadcrumbs(element);
  } catch (e) {
    lanaLog({ e, message: 'Breadcrumbs failed fetching base', tags: 'gnav-breadcrumbs', errorType: 'info' });
    return null;
  }
};

const fromUrl = () => {
  if (getMetadata(metadata.fromUrl) !== 'on') return null;
  const list = toFragment`<ul></ul>`;
  const paths = document.location.pathname
    .replace((getConfig().locale?.prefix || ''), '')
    .split('/')
    .filter((n) => n);

  for (let i = 0; i < paths.length; i += 1) {
    list.append(toFragment`
      <li>
        <a href="/${paths.slice(0, i + 1).join('/')}">${paths[i].replaceAll('-', ' ')}</a>
      </li>
    `);
  }
  return createBreadcrumbs(toFragment`<div>${list}</div>`);
};

export default async function init(el) {
  try {
    const breadcrumbsEl = await createWithBase(el) || createBreadcrumbs(el) || fromUrl();
    setBreadcrumbSEO(breadcrumbsEl);
    return breadcrumbsEl;
  } catch (e) {
    lanaLog({ e, message: 'Breadcrumbs failed rendering', tags: 'gnav-breadcrumbs', errorType: 'error' });
    return null;
  }
}

