const params = new Proxy(
  // eslint-disable-next-line compat/compat
  new URLSearchParams(window.location.search),
  { get: (searchParams, prop) => searchParams.get(prop) },
);

let appReferrer = params.x_api_client_id || params['x-product'] || '';
if (params.x_api_client_location || params['x-product-location']) {
  appReferrer = `${appReferrer}:${params.x_api_client_location || params['x-product-location']}`;
}
let trackingId = params.trackingid || '';
if (params.mv) {
  trackingId = `${trackingId}:${params.mv}`;
}
if (params.mv2) {
  trackingId = `${trackingId}:${params.mv2}`;
}
const appTags = [];
if (params.workflow) {
  appTags.push(params.workflow);
}
if (params.dropzone2) {
  appTags.push('dropzone2');
}

function ensureSatelliteReady(callback) {
  // eslint-disable-next-line no-underscore-dangle
  if (window._satellite?.track instanceof Function) {
    callback();
  } else {
    setTimeout(() => ensureSatelliteReady(callback), 50);
  }
}

function getSessionID() {
  const aToken = window.adobeIMS.getAccessToken();
  const arrayToken = aToken?.token.split('.');
  if (!arrayToken) return;
  const tokenPayload = JSON.parse(atob(arrayToken[1]));
  // eslint-disable-next-line consistent-return
  return tokenPayload.sub || tokenPayload.user_id;
}

function eventData(metaData, { appReferrer: referrer, trackingId: tracking }) {
  const {
    verb, eventName, errorInfo = '', noOfFiles, uploadTime, type, size, count, userAttempts,
  } = metaData;

  return {
    event: {
      pagename: `acrobat:verb-${verb}:${eventName}${errorInfo ? ` ${errorInfo}` : ''}`,
      ...(noOfFiles ? { no_of_files: noOfFiles } : {}),
      ...(uploadTime ? { uploadTime } : {}),
    },
    content: { type, size, count, fileType: type, totalSize: size },
    source: {
      user_agent: navigator.userAgent,
      lang: document.documentElement.lang,
      app_name: `unity:${verb}`,
      url: window.location.href,
      referrer,
      tracking,
    },
    user: {
      locale: document.documentElement.lang.toLocaleLowerCase(),
      id: getSessionID(),
      is_authenticated: `${window.adobeIMS?.isSignedInUser() ? 'true' : 'false'}`,
      user_tags: [`${localStorage['unity.user'] ? 'frictionless_return_user' : 'frictionless_new_user'}`],
      ...(userAttempts && { return_user_type: userAttempts }),
    },
  };
}

function createPayloadForSplunk(metaData) {
  const {
    verb, eventName, noOfFiles, uploadTime, name, type, size, count, uploadType, userAttempts, errorData
  } = metaData;

  return {
    event: {
      name: eventName,
      category: "acrobat",
      subcategory: verb,
      ...(uploadTime && { uploadTime }),
      ...(uploadType && { uploadType })
    },
    content: { name, type, size, count, fileType: type, totalSize: size,
      ...(noOfFiles && { no_of_files: noOfFiles }),
    },
    source: {
      user_agent: navigator.userAgent,
      lang: document.documentElement.lang,
      app_name: `unity`,
      url: window.location.href
    },
    user: {
      locale: document.documentElement.lang.toLocaleLowerCase(),
      id: getSessionID(),
      isAuthenticated: `${window.adobeIMS?.isSignedInUser() ? 'true' : 'false'}`,
      type: [`${localStorage['unity.user'] ? 'frictionless_return_user' : 'frictionless_new_user'}`],
      ...(userAttempts && { return_user_type: userAttempts }),
    },
    error: errorData ? {
      type: errorData.code,
      ...(errorData.subCode && { subCode: errorData.subCode }),
      ...(errorData.desc && { desc: errorData.desc }),
    } : undefined,
  };
}

export function sendAnalyticsToSplunk(eventName, verb, metaData, splunkEndpoint) {
  try {
    const eventDataPayload = createPayloadForSplunk({ ...metaData, eventName, verb });
    fetch(splunkEndpoint, {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(eventDataPayload),
    });
  } catch(error) {
    window.lana?.log(
      `An error occurred while sending ${eventName} to splunk, verb: ${verb}, metadata: ${metaData}, error: ${error}`,
      { sampleRate: 100, tags: 'DC_Milo,Project Unity (DC)' },
    );
  }
}

export function createEventObject(eventName, verb, metaData, trackingParams, documentUnloading) {
  const verbEvent = `acrobat:verb-${verb}:${eventName}`;
  const eventDataPayload = eventData({ ...metaData, eventName, verb }, trackingParams);
  const redirectReady = new CustomEvent('DCUnity:RedirectReady');

  return {
    documentUnloading,
    // eslint-disable-next-line
    done: function (AJOPropositionResult, error) {
      if (!documentUnloading) {
        if (eventName === 'job:uploaded') {
          window.dispatchEvent(redirectReady);
        }
        const accountType = window?.adobeIMS?.getAccountType();
        if (error) {
          window.lana?.log(
            `Error Code: ${error}, Status: 'Unknown', Message: An error occurred while sending ${verbEvent}, Account Type: ${accountType}`,
            { sampleRate: 100, tags: 'DC_Milo,Project Unity (DC)' },
          );
        }
      }
    },
    data: {
      eventType: 'web.webinteraction.linkClicks',
      web: {
        webInteraction: {
          linkClicks: { value: 1 },
          type: 'other',
          name: verbEvent,
        },
      },
      _adobe_corpnew: {
        digitalData: {
          primaryEvent: {
            eventInfo: {
              eventName: `${verbEvent}${metaData.errorInfo ? ` ${metaData.errorInfo}` : ''}`,
              value: `${verb} - Frictionless to Acrobat Web`,
            },
          },
          dcweb: eventDataPayload,
          dcweb2: eventDataPayload,
        },
      },
    },
  };
}

export default function init(eventName, verb, metaData, documentUnloading = true) {
  const trackingParams = { appReferrer, trackingId };

  const trackEvent = () => {
    const event = createEventObject(eventName, verb, metaData, trackingParams, documentUnloading);
    // eslint-disable-next-line no-underscore-dangle
    window._satellite.track('event', event);
    window.alloy_getIdentity
      .then((value) => {
        window.ecid = value.identity.ECID;
      });
  };

  // eslint-disable-next-line no-underscore-dangle
  if (window._satellite?.track instanceof Function) {
    // If satellite is already ready, just track immediately
    trackEvent();
  } else {
    // Otherwise, keep waiting until _satellite is ready
    // This should be just a 50 milliseconds delay
    ensureSatelliteReady(trackEvent);
  }
}

export function reviewAnalytics(verb) {
  // eslint-disable-next-line no-underscore-dangle
  if (window._satellite?.track instanceof Function) {
    import('../frictionless.js').then((mod) => {
      mod.default(verb);
    });
  } else {
    ensureSatelliteReady(() => {
      import('../frictionless.js').then((mod) => {
        mod.default(verb);
      });
    });
  }
}

/* media - consonant v6 */

import {
  decorateBlockBg,
  decorateBlockText,
  getBlockSize,
  decorateTextOverrides,
  applyHoverPlay,
  loadCDT,
} from '../../utils/decorate.js';
import { createTag, loadStyle, getConfig } from '../../utils/utils.js';

const blockTypeSizes = {
  small: ['xs', 's', 'm'],
  medium: ['m', 's', 'm'],
  'medium-compact': ['xl', 'm', 'l'],
  large: ['xl', 'm', 'l'],
  xlarge: ['xxl', 'm', 'l'],
};

function decorateAvatar(el) {
  // is the first row a picture only
  const childElements = el.children[0]?.children;
  if (childElements.length !== 1) return;
  [...childElements].forEach((e, i) => {
    if (e.localName !== null && e.localName === 'picture') childElements[i].classList.add('avatar');
  });
}

function decorateQr(el) {
  const text = el.querySelector('.text');
  if (!text) return;
  const appStore = text.children[(text.children.length - 1)]?.querySelector('a');
  const googlePlay = text.children[(text.children.length - 2)]?.querySelector('a');
  const qrImage = text.children[(text.children.length - 3)];
  if (!qrImage || !appStore || !googlePlay) return;
  [appStore, googlePlay].forEach(({ parentElement }) => {
    parentElement.classList.add('qr-button-container');
  });
  qrImage.classList.add('qr-code-img');
  appStore.classList.add('app-store');
  appStore.textContent = '';
  appStore.setAttribute('aria-label', 'Apple App Store');
  googlePlay.classList.add('google-play');
  googlePlay.textContent = '';
  googlePlay.setAttribute('aria-label', 'Google Play Store');
}

export default async function init(el) {
  if (el.className.includes('rounded-corners')) {
    const { miloLibs, codeRoot } = getConfig();
    const base = miloLibs || codeRoot;
    loadStyle(`${base}/styles/rounded-corners.css`);
  }
  el.classList.add('con-block');
  let rows = el.querySelectorAll(':scope > div');
  if (rows.length > 1) {
    el.classList.add('has-bg');
    const [head, ...tail] = rows;
    decorateBlockBg(el, head);
    rows = tail;
  }
  let blockType = null;
  const types = ['merch', 'qr-code'];
  [...types].forEach((type) => {
    if (!el.classList.contains(type)) return;
    blockType = type;
  });
  const size = getBlockSize(el);
  const container = createTag('div', { class: 'container foreground' });

  rows.forEach((row) => {
    row.classList.add('media-row');
    const header = row.querySelector('h1, h2, h3, h4, h5, h6');
    if (header) {
      const text = header.closest('div');
      text.classList.add('text');
      decorateAvatar(text);
      decorateBlockText(text, blockTypeSizes[size], blockType);
    }
    const image = row.querySelector(':scope > div:not([class])');
    image?.classList.add('image');
    const imageVideo = image?.querySelector('video');
    if (imageVideo) applyHoverPlay(imageVideo);

    // subcopy
    const actionArea = row.querySelector('p.action-area');
    if (actionArea?.nextElementSibling?.tagName === 'P') {
      actionArea.nextElementSibling.className = 'subcopy';
    }

    // subcopy with links
    if (actionArea?.nextElementSibling?.tagName === 'H3') {
      actionArea.nextElementSibling.classList.remove('heading-m', 'body-xl');
      actionArea.nextElementSibling.classList.add('heading-xs');
      const links = row.querySelectorAll('h3.heading-xs ~ p.body-s a, h3.heading-xs ~ p.icon-area a');
      links.forEach((link) => {
        link.parentElement.className = 'subcopy-link';
        link.className = 'body-xxs';
      });
    }
    const lastActionArea = el.querySelector('.action-area:last-of-type');
    if (lastActionArea) {
      const div = createTag('div', { class: 'cta-container' });
      lastActionArea.insertAdjacentElement('afterend', div);
      if (lastActionArea.previousElementSibling.className.includes('icon-stack-area')) {
        div.append(lastActionArea.previousElementSibling);
      }
      div.append(lastActionArea);
    }
    container.append(row);
  });

  if (blockType === 'qr-code') decorateQr(container);
  el.append(container);
  const mediaRowReversed = el.querySelector(':scope > .foreground > .media-row > div').classList.contains('text');
  if (mediaRowReversed) el.classList.add('media-reverse-mobile');
  decorateTextOverrides(el);

  if (el.classList.contains('countdown-timer')) {
    const textBlock = container.querySelector('.text');
    if (textBlock) await loadCDT(textBlock, el.classList);
  }
}

