.tk-adobe-clean { font-family: "adobe-clean",sans-serif; }

/* Custom properties (variables) */
:root {
  /* Top navigation - box model */
  --feds-height-nav: 63px;
  --feds-maxWidth-nav: 1440px;
  --feds-minWidth-menu: 1200px;
  --feds-height-breadcrumbs: 33px;
  --feds-gutter: 8px;
  /* Top navigation - backgrounds */
  --feds-background-nav: #f8f8f8;
  --feds-background-popup: #f3f3f3;
  --feds-background-promo: #fff;
  --feds-background-promo--dark: #000;
  /* Top navigation - borders */
  --feds-borderColor: #eaeaea;
  --feds-borderColor-menu: #e1e1e1;
  --feds-borderColor-navLink: #2c2c2c;
  /* Top navigation - colors */
  --feds-color-adobeBrand: #eb1000;
  --feds-color-headline: #505050;
  --feds-color-hamburger: #2d2d2d;
  --feds-color-breadcrumbs--current: #2c2c2c;
  --feds-color-signIn: #4b4b4b;
  /* Top navigation - misc */
  --feds-radius-utilityIcon: 4px;
  /* Links */
  --feds-background-link--hover: #e9e9e9;
  --feds-borderColor-link: #e9e9e9;
  --feds-color-link: #292929;
  --feds-color-blue-link: #274dea;
  --feds-color-link--hover: #1d3ecf;
  --feds-color-navLink-description: #505050;
  --feds-color-link-breadcrumbs: #707070;
  /* Footer */
  --feds-background-footer: #fafafa;
  --feds-borderColor-featuredProducts: #999;
  --feds-gutter-footer: 32px;

  /* mobile gnav redesign */
  --feds-color-white-v2: #fff;
  --feds-color-black-v2: #000;
  --feds-background-cloudmenu-v2: #111;
  --feds-borderColor-link-v2: #eaeaea;
  --feds-backgroundColor-tabs-v2: #f8f8f8;
  --feds-backgroundColor-tabContent-v2: #f3f3f3;
  --feds-borderColor-localnav-v2: #eee;

  /* App banner */
  --app-banner-height: 76px;
}

/* Nav Link styles */
.feds-navLink {
  display: flex;
  align-items: center;
  padding: 12px;
  border: none;
  color: var(--feds-color-link);
  font: inherit;
  white-space: nowrap;
  flex-shrink: 0;
}

.feds-navLink:hover,
.feds-navLink:focus {
  color: var(--feds-color-link--hover);
}

.feds-navLink:not(.feds-navLink--hoverCaret) {
  column-gap: 15px;
}

.feds-navLink--blue {
  color: var(--feds-color-blue-link);
}

.feds-navLink--hoverCaret {
  position: relative;
  padding-right: 32px;
}

.feds-navLink--hoverCaret:hover,
.feds-navLink--hoverCaret:focus {
  color: var(--feds-color-link);
}

.feds-navLink--hoverCaret:after,
.feds-navLink[class *= '-gradient'] .feds-navLink-title:after {
  display: flex;
  border-width: 0 1px 1px 0;
  border-style: solid;
  border-color: var(--feds-color-link);
  transform-origin: 75% 75%;
  transition: transform 0.1s ease;
  box-sizing: content-box;
}

.feds-navLink--hoverCaret:after {
  position: absolute;
  right: 18px;
  top: 50%;
  width: 6px;
  height: 6px;
  margin-top: -3px;
  transform: rotateZ(45deg);
  content: "";
}

[dir = "rtl"] .feds-navLink--hoverCaret {
  padding-right: 20px;
  padding-left: 32px;
}

[dir = "rtl"] .feds-navLink--hoverCaret:after {
  right: unset;
  left: 18px;
}

.feds-navLink[class *= '-gradient'] {
  display: none;
}

.feds-navLink-image,
.feds-navLink-description {
  display: none;
}

.feds-navLink-title {
  display: flex;
  align-items: center;
}

header.global-navigation.ready {
  visibility: visible !important;
}

/* Desktop styles */
@media (min-width: 900px) {
  .feds-navLink,
  .feds-navLink--hoverCaret,
  [dir = "rtl"] .feds-navLink--hoverCaret {
    padding: 0 12px;
  }

  .feds-navItem--active > .feds-navLink {
    position: relative;
    font-weight: 700;
  }

  .feds-navItem--active > .feds-navLink:only-child {
    cursor: default;
  }

  .feds-navItem--active > .feds-navLink:only-child:hover,
  .feds-navItem--active > .feds-navLink:only-child:focus {
    color: var(--feds-color-link);
  }

  .feds-navItem--active > .feds-navLink:before {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    border-bottom: 2px solid var(--feds-borderColor-navLink);
    content: "";
  }

  .feds-navItem--active > .feds-navLink:before {
    margin: 0 12px;
  }

  .feds-navItem--activeDeferred > .feds-navLink {
    padding: 0;
    width: 100%;
    justify-content: center;
  }

  .feds-topnav--overflowing .feds-navLink,
  .feds-topnav--overflowing .feds-navLink--hoverCaret,
  [dir = "rtl"] .feds-topnav--overflowing .feds-navLink--hoverCaret {
    padding: 0 8px;
  }

  .feds-topnav--overflowing .feds-navItem--active > .feds-navLink:before {
    margin: 0 8px;
  }

  .feds-topnav--overflowing .feds-navItem--activeDeferred > .feds-navLink {
    padding: 0;
  }

  .feds-navLink--hoverCaret:hover,
  .feds-navLink--hoverCaret:focus {
    background-color: var(--feds-background-popup);
  }

  .feds-navLink--hoverCaret:after {
    position: static;
    margin-top: 0;
    margin-left: 5px;
  }

  .feds-topnav--overflowing .feds-navLink--hoverCaret:after {
    margin-left: 3px;
  }

  [dir = "rtl"] .feds-navLink--hoverCaret:after {
    margin-left: 0;
    /* Margin different than LTR due to transform origin effect */
    margin-right: 7px;
  }

  [dir = "rtl"] .feds-topnav--overflowing .feds-navLink--hoverCaret:after {
    margin-left: 0;
    /* Margin different than LTR due to transform origin effect */
    margin-right: 5px;
  }

  .feds-navLink-image {
    display: flex;
  }

  .feds-navLink-image picture,
  .feds-navLink-image img {
    display: block;
  }

  .feds-navLink-image picture {
    width: max-content;
    max-width: 25px;
  }

  .feds-navLink-description {
    display: flex;
    font-size: 12px;
    color: var(--feds-color-navLink-description);
  }

  .feds-navLink:hover .feds-navLink-description,
  .feds-navLink:focus .feds-navLink-description {
    color: var(--feds-color-navLink-description);
  }

  /* Nav Link special styles for A/B test */
  .feds-navLink[class *= '-gradient'] {
    display: flex;
    border-radius: 4px;
    font-weight: 600;
  }

  .feds-menu-column--group .feds-navLink[class *= '-gradient'] {
    padding-left: 8px;
    padding-right: 8px;
    column-gap: 8px;
  }

  .feds-navLink[class *= '-gradient']:not(:first-child) {
    margin-top: 12px;
  }

  .feds-menu-column--group .feds-navLink[class *= '-gradient'] picture {
    max-width: 18px;
  }

  .feds-navLink[class *= '-gradient'] .feds-navLink-title {
    column-gap: 4px;
  }

  .feds-navLink[class *= '-gradient'] .feds-navLink-title:after {
    width: 4px;
    height: 4px;
    transform: rotate(-45deg);
    content: '';
  }

  .feds-navLink.feds-navLink--header[class *= '-gradient'] .feds-navLink-title:after {
    display: none;
  }

  .feds-navLink.feds-navLink--header {
    pointer-events: none;
  }

  [dir = "rtl"] .feds-navLink[class *= '-gradient'] .feds-navLink-title:after {
    transform: rotate(135deg);
  }

  .feds-navLink[class *= '-gradient']:hover .feds-navLink-title:after,
  .feds-navLink[class *= '-gradient']:focus .feds-navLink-title:after {
    border-color: var(--feds-color-link--hover);
  }

  .feds-navLink--photo-gradient {
    background: linear-gradient(90deg, #d0e8fa, #cef4f4);
  }

  .feds-navLink--design-gradient {
    background: linear-gradient(90deg, #fccbfc, #ffe9d0);
  }

  .feds-navLink--3d-gradient {
    background: linear-gradient(90deg, #e1f5cb, #edefb5);
  }

  .feds-navLink--pdf-gradient {
    background: linear-gradient(90deg, #ffbfbf, #fde6d3);
  }

  .feds-navLink--video-gradient {
    background: linear-gradient(90deg, #dcd9ff, #d5f1fd);
  }

  .feds-navLink--ai-gradient {
    background: linear-gradient(90deg, #bce3ff, #ffe9d3, #f8d5e4);
  }

  .feds-navLink--gray-gradient {
    background: #eaeaea;
  }
}

.columns > .row {
  display: grid;
  gap: var(--spacing-m);
  margin-bottom: var(--spacing-xs);
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
}

.columns.contained {
  max-width: var(--grid-container-width);
  margin: 0 auto;
}

.columns.contained.middle {
  align-items: center;
}

.columns.xxl-spacing {
  gap: var(--spacing-xxl);
}

.columns.xl-spacing {
  gap: var(--spacing-xl);
}

.columns.l-spacing {
  gap: var(--spacing-l);
}

.columns.s-spacing {
  gap: var(--spacing-s);
}

.columns.xs-spacing {
  gap: var(--spacing-xs);
}

/* Table */
.columns.columns-table {
  font-size: 14px;
}

.columns.columns-table > .row {
  margin-bottom: 0;
  padding: var(--spacing-xs) 0;
  grid-template-columns: repeat(auto-fit, minmax(0, 1fr));
  border-bottom: 1px solid #D5D5D5;
  align-items: center;
}

.columns.columns-table > .row:hover {
  background-color: #0000000f;
}

.columns.columns-table > .row:first-child {
  text-transform: uppercase;
  font-size: 11px;
  font-weight: bold;
  letter-spacing: 1px;
}

/* For desktop only */
@media (min-width:600px) {
  .columns.columns-table > .row {
    padding: var(--spacing-xs);
  }
}

export default function init(el) {
  const rows = el.querySelectorAll(':scope > div');
  rows.forEach((row, rdx) => {
    row.className = `row row-${rdx + 1}`;
    const cols = row.querySelectorAll(':scope > div');
    cols.forEach((col, cdx) => {
      col.className = `col col-${cdx + 1}`;
    });
  });
  if (el.classList.contains('table')) {
    el.classList.add('columns-table');
    el.classList.remove('table');
  }
}

/* Base */
.global-footer {
  padding: 20px 0;
  display: flex;
  justify-content: center;
  font-size: 12px;
  background: var(--feds-background-footer);
}

.global-footer ul {
  list-style-type: none;
}

.global-footer a {
  text-decoration: unset;
}

.global-footer a:hover {
  text-decoration: none;
}

.global-footer button {
  background: none;
  cursor: pointer;
}

.feds-footer-wrapper {
  width: 100%;
  max-width: var(--feds-maxWidth-nav);
  display: flex;
  flex-direction: column;
  row-gap: 30px;
  color: var(--feds-color-headline);
}

.feds-footer-wrapper ul {
  margin: 0;
  padding: 0;
}

.feds-footer-icons {
  display: none;
}

/* Menu */
.feds-footer-wrapper .feds-menu-headline {
  font-size: 14px;
  line-height: 1.4;
}

.feds-footer-wrapper .feds-menu-column {
  display: flex;
  flex-direction: column;
}

/* Additional nesting is needed for specificity */
.feds-footer-wrapper .feds-menu-content .feds-navLink {
  padding-top: 0;
  padding-bottom: 0;
}

/* Featured products */
.feds-featuredProducts {
  display: none;
  justify-content: center;
  align-items: center;
  column-gap: 12px;
  padding: 8px 0;
  margin: 0 var(--feds-gutter-footer);
  border-width: 1px 0 1px 0;
  border-style: solid;
  border-color: var(--feds-borderColor-featuredProducts);
}

.feds-featuredProducts-label {
  flex-shrink: 0;
  font-size: 14px;
  font-weight: 700;
}

/* Options (region selector, social, privacy) */
.feds-footer-options,
.feds-footer-miscLinks,
.feds-regionPicker {
  display: flex;
  align-items: center;
}

.feds-footer-options {
  justify-content: space-between;
  align-items: flex-start;
  padding: 0 var(--feds-gutter-footer);
  flex-wrap: wrap;
  row-gap: 12px;
  column-gap: 40px;
}

.feds-footer-miscLinks {
  column-gap: 30px;
  flex-shrink: 0;
}

/* Region picker */
.feds-regionPicker {
  column-gap: 10px;
  color: var(--feds-color-link);
  border-color: var(--feds-color-link);
  fill: var(--feds-color-link);
}

.feds-regionPicker:hover {
  color: var(--feds-color-link--hover);
  border-color: var(--feds-color-link--hover);
  fill: var(--feds-color-link--hover);
}

.feds-regionPicker:after {
  width: 6px;
  height: 6px;
  border-width: 0 1px 1px 0;
  border-style: solid;
  border-color: inherit;
  transform-origin: 75% 75%;
  transform: rotateZ(45deg);
  transition: transform 0.1s ease;
  content: "";
}

.feds-regionPicker[aria-expanded = "true"]:after {
  transform: rotateZ(-135deg);
}

.feds-regionPicker-globe {
  width: 20px;
  height: 20px;
  fill: inherit;
}

.feds-regionPicker-wrapper {
  position: relative;
}

.feds-regionPicker-wrapper > .fragment {
  position: absolute;
  bottom: calc(100% + 10px);
  left: 0;
  display: none;
  padding: 20px;
  min-width: 130px;
  max-height: 300px;
  overflow-y: auto;
  background: var(--feds-background-nav);
  border: 1px solid var(--feds-borderColor);
  border-radius: 4px;
}

[dir = "rtl"] .feds-regionPicker-wrapper > .fragment {
  left: auto;
  right: 0;
}

.feds-regionPicker[aria-expanded = "true"] + .fragment {
  display: block;
}

/* Styling inline region selector dropdown to maintain backwards compatibility */
.feds-regionPicker-wrapper .fragment.link-block {
  display: none; /* Hide fragment placeholder until it is loaded */
}

.feds-regionPicker-wrapper > .fragment .region-selector {
  padding: 0;
}

.feds-regionPicker-wrapper > .fragment .content {
  display: flex;
  flex-direction: column;
}

.feds-regionPicker-wrapper > .fragment p { /* Usually the title */
  margin-top: 0;
  margin-bottom: 10px;
}

.feds-regionPicker-wrapper > .fragment ul { /* Usually regions wrapper */
  margin: 0 -20px;
}

.feds-regionPicker-wrapper > .fragment a { /* Usually a region */
  padding: 0 20px;
  display: block;
  line-height: 2;
  color: var(--feds-color-link--hover);
}

.feds-regionPicker-wrapper > .fragment a:hover {
  background: var(--feds-background-link--hover);
}

/* Social */
.feds-social {
  display: flex;
  column-gap: 15px;
}

.feds-social-link {
  display: block;
}

.feds-social-icon {
  display: block;
  width: 20px;
  height: 20px;
  color: #808080;
}

.feds-social-link:hover .feds-social-icon {
  color: #909090;
}

/* Privacy */
.feds-footer-legalWrapper {
  display: flex;
  flex-direction: column;
  row-gap: 12px;
}

.feds-footer-privacySection {
  display: flex;
  flex-wrap: wrap;
  margin: 0;
}

.feds-footer-privacyLink {
  display: inline-flex;
  column-gap: 5px;
  align-items: center;
  color: var(--feds-color-link);
  fill: var(--feds-color-link);
  vertical-align: bottom;
}

span.feds-footer-privacyLink-divider {
    display: inline-block;
    margin: 0 5px;
}

.feds-footer-privacyLink:hover {
  color: var(--feds-color-link--hover);
  fill: var(--feds-color-link--hover);
}

.feds-adChoices-icon {
  width: 12px;
  height: 12px;
}

@media (min-width: 600px) {
  dialog.feds-dialog {
    max-width: 80vw;
    width: fit-content;
  }
}

@media (min-width: 1200px) {
  dialog.feds-dialog {
    width: 1200px;
    max-width: calc((100% - 6px) - 2em);
  }
}

@media (min-width: 900px) {
  /* If there is too much content, float it on multiple rows */
  .feds-footer-wrapper .feds-menu-content {
    flex-wrap: wrap;
    width: auto;
    column-gap: 12px;
  }

  .feds-footer-wrapper .feds-menu-headline {
    border: none;
  }

  .feds-footer-wrapper .feds-menu-column {
    row-gap: 15px;
  }

  .feds-footer-wrapper .feds-menu-column:nth-child(-n+3):last-child {
    flex-grow: 1;
  }

  .feds-footer-wrapper .feds-menu-column:nth-last-child(-n+3):first-child .feds-menu-section,
  .feds-footer-wrapper .feds-menu-column:nth-child(-n+3):last-child .feds-menu-section,
  .feds-footer-wrapper .feds-menu-column:nth-child(2):nth-last-child(2) .feds-menu-section {
    width: fit-content;
  }

  .feds-footer-wrapper .feds-menu-column:nth-last-child(-n+3):first-child,
  .feds-footer-wrapper .feds-menu-column:nth-child(2):nth-last-child(2) {
    min-width: 20%;
  }

  /* Featured products */
  .feds-featuredProducts {
    display: flex;
  }

  /* Options (region selector, social, privacy) */
  .feds-footer-options {
    flex-wrap: nowrap;
  }

  /* Privacy */
  .feds-footer-legalWrapper {
    align-items: flex-end;
    text-align: right;
  }

  .feds-footer-privacySection {
    justify-content: flex-end;
    text-align: right;
  }
  span.feds-footer-privacyLink-divider, .feds-footer-privacyLink {
    margin-left: 5px;
    margin-right: 0;
  }
}

/* max-width (1440) + 2 x gutter (32) */
@media (min-width: 1504px) {
  .feds-footer-wrapper {
    max-width: 1504px;
  }
}

/* eslint-disable no-async-promise-executor */
import {
  loadBlock,
  decorateAutoBlock,
  decorateLinks,
  getMetadata,
  getConfig,
  localizeLink,
  loadStyle,
  getFederatedUrl,
  getFedsPlaceholderConfig,
} from '../../utils/utils.js';

import {
  getExperienceName,
  getAnalyticsValue,
  loadDecorateMenu,
  fetchAndProcessPlainHtml,
  loadBaseStyles,
  yieldToMain,
  lanaLog,
  logErrorFor,
  toFragment,
  federatePictureSources,
  isDarkMode,
} from '../global-navigation/utilities/utilities.js';

import { replaceKey } from '../../features/placeholders.js';

const { miloLibs, codeRoot, locale, mep } = getConfig();
const base = miloLibs || codeRoot;

const CONFIG = {
  socialPlatforms: ['facebook', 'instagram', 'twitter', 'linkedin', 'pinterest', 'discord', 'behance', 'youtube', 'weibo', 'social-media'],
  delays: { decoration: 3000 },
};

class Footer {
  constructor({ block } = {}) {
    this.block = block;
    this.elements = {};
    this.init();
  }

  init = () => logErrorFor(async () => {
    // We initialize the footer decoration logic when either 3s have passed
    // OR when the footer element is close to coming into view
    let decorationTimeout;

    // Set up intersection observer
    const intersectionOptions = { rootMargin: '300px 0px' };

    const observer = new window.IntersectionObserver((entries) => {
      const isIntersecting = entries.find((entry) => entry.isIntersecting === true);

      if (isIntersecting) {
        clearTimeout(decorationTimeout);
        observer.disconnect();
        this.decorateContent();
      }
    }, intersectionOptions);

    observer.observe(this.block);

    // Set timeout after which we load the footer automatically
    decorationTimeout = setTimeout(() => {
      observer.disconnect();
      this.decorateContent();
    }, CONFIG.delays.decoration);
  }, 'Error in global footer init', 'global-footer', 'error');

  decorateContent = () => logErrorFor(async () => {
    // Fetch footer content
    const url = getMetadata('footer-source') || `${locale.contentRoot}/footer`;
    this.body = await fetchAndProcessPlainHtml({
      url,
      shouldDecorateLinks: false,
    });

    if (!this.body) {
      const error = new Error('Could not create global footer. Content not found!');
      error.tags = 'global-footer';
      error.url = url;
      error.errorType = 'error';
      lanaLog({ message: error.message, ...error });
      const { onFooterError } = getConfig();
      onFooterError?.(error);
      return;
    }

    const [region, social] = ['.region-selector', '.social'].map((selector) => this.body.querySelector(selector));
    const [regionParent, socialParent] = [region?.parentElement, social?.parentElement];
    // We remove and add again the region and social elements from the body to make sure
    // they don't get decorated twice
    [regionParent, socialParent].forEach((parent) => parent?.replaceChildren());

    decorateLinks(this.body);

    regionParent?.appendChild(region);
    socialParent?.appendChild(social);

    // Support auto populated modal
    await Promise.all([...this.body.querySelectorAll('.modal')].map(loadBlock));

    const path = getFederatedUrl(url);
    federatePictureSources({ section: this.body, forceFederate: path.includes('/federal/') });

    // Order is important, decorateFooter makes use of elements
    // which have already been created in previous steps
    const tasks = [
      loadBaseStyles,
      this.decorateGrid,
      this.decorateProducts,
      this.loadIcons,
      this.decorateRegionPicker,
      this.decorateSocial,
      this.decoratePrivacy,
      this.decorateFooter,
    ];

    for await (const task of tasks) {
      await yieldToMain();
      await task();
    }

    const mepMartech = mep?.martech || '';
    this.block.setAttribute('daa-lh', `gnav|${getExperienceName()}|footer${mepMartech}`);
    this.block.append(this.elements.footer);
    const { onFooterReady } = getConfig();
    onFooterReady?.();
  }, 'Failed to decorate footer content', 'global-footer', 'error');

  loadMenuLogic = async () => {
    this.menuLogic = this.menuLogic || new Promise(async (resolve) => {
      const menuLogic = await loadDecorateMenu();
      this.decorateMenu = menuLogic.decorateMenu;
      this.decorateLinkGroup = menuLogic.decorateLinkGroup;
      resolve();
    });

    return this.menuLogic;
  };

  decorateGrid = async () => {
    this.elements.footerMenu = '';
    const columns = this.body.querySelectorAll(':scope > div > h2:first-child');

    if (!columns || !columns.length) return this.elements.footerMenu;

    this.elements.footerMenu = toFragment`<div class="feds-menu-content"></div>`;
    columns.forEach((column) => this.elements.footerMenu.appendChild(column.parentElement));

    await this.loadMenuLogic();

    await this.decorateMenu({
      item: this.elements.footerMenu,
      type: 'footerMenu',
    });

    this.elements.headlines = this.elements.footerMenu.querySelectorAll('.feds-menu-headline');

    return this.elements.footerMenu;
  };

  loadIcons = async () => {
    const file = await fetch(`${base}/blocks/global-footer/icons.svg`);
    if (!file.ok) {
      lanaLog({
        message: 'Issue with loadIcons',
        e: `${file.statusText} url: ${file.url}`,
        tags: 'global-footer',
        errorType: 'info',
      });
    }
    const content = await file.text();
    const elem = toFragment`<div class="feds-footer-icons">${content}</div>`;
    this.block.append(elem);
  };

  decorateProducts = async () => {
    this.elements.featuredProducts = '';

    // Get the featured products wrapper by looking for a link group's parent
    const featuredProductElem = this.body.querySelector('.link-group');
    if (!featuredProductElem) return this.elements.featuredProducts;

    const featuredProductsContent = featuredProductElem.parentElement;
    this.elements.featuredProducts = toFragment`<div class="feds-featuredProducts"></div>`;

    const [placeholder] = await Promise.all([
      replaceKey('featured-products', getFedsPlaceholderConfig()),
      this.loadMenuLogic(),
    ]);

    if (placeholder && placeholder.length) {
      this.elements.featuredProducts
        .append(toFragment`<span class="feds-featuredProducts-label" role="heading" aria-level="2">${placeholder}</span>`);
    }

    featuredProductsContent.querySelectorAll('.link-group').forEach((linkGroup) => {
      this.elements.featuredProducts.append(this.decorateLinkGroup(linkGroup));
    });

    return this.elements.featuredProducts;
  };

  decorateRegionPicker = async () => {
    this.elements.regionPicker = '';
    const regionSelector = this.body.querySelector('.region-selector a');
    if (!regionSelector) return this.elements.regionPicker;

    let url;

    try {
      url = new URL(regionSelector.href);
    } catch (e) {
      lanaLog({ message: `Could not create URL for region picker; href: ${regionSelector.href}`, tags: 'global-footer', errorType: 'error' });
      return this.elements.regionPicker;
    }

    const regionPickerClass = 'feds-regionPicker';
    const regionPickerTextElem = toFragment`<span class="feds-regionPicker-text">${regionSelector.textContent}</span>`;
    const regionPickerElem = toFragment`
      <a
        href="${regionSelector.href}"
        class="${regionPickerClass}"
        role="button">
        <svg aria-hidden="true" xmlns="http://www.w3.org/2000/svg" class="feds-regionPicker-globe" focusable="false">
          <use href="#footer-icon-globe" />
        </svg>
        ${regionPickerTextElem}
      </a>`;
    regionPickerElem.dataset.modalPath = `${url.pathname}#_inline`;
    regionPickerElem.dataset.modalHash = url.hash;
    const regionPickerWrapperClass = 'feds-regionPicker-wrapper';
    this.elements.regionPicker = toFragment`<div class="${regionPickerWrapperClass}">
        ${regionPickerElem}
      </div>`;

    const isRegionPickerExpanded = () => regionPickerElem.getAttribute('aria-expanded') === 'true';

    // Note: the region picker currently works only with Milo modals/fragments;
    // in the future we'll need to update this for non-Milo consumers
    if (url.hash !== '') {
      // Hash -> region selector opens a modal
      decorateAutoBlock(regionPickerElem); // add modal-specific attributes
      regionPickerElem.href = url.hash;
      loadStyle(`${base}/blocks/modal/modal.css`);
      const { default: initModal } = await import('../modal/modal.js');
      const modal = await initModal(regionPickerElem);

      const loadRegionNav = async () => {
        const block = document.querySelector('.region-nav');
        if (block && getConfig().standaloneGnav) {
          // on standalone the region-nav will fail to load automatically through
          // the modal calling fragment.js. In that case we will have data-failed=true
          // and we should manually load region nav
          // If that's not the case then we're not a standalone gnav
          // and we mustn't load region-nav twice.
          if (block.getAttribute('data-failed') !== 'true') return;
          block.classList.add('hide');
          loadStyle(`${base}/blocks/region-nav/region-nav.css`);
          const { default: initRegionNav } = await import('../region-nav/region-nav.js');
          initRegionNav(block);
          // decoratePlaceholders(block, getConfig());
          block.classList.remove('hide');
        }
      };

      if (modal) await loadRegionNav(); // just in case the modal is already open

      regionPickerElem.addEventListener('click', () => {
        if (!isRegionPickerExpanded()) {
          regionPickerElem.setAttribute('aria-expanded', 'true');
          // wait for the modal to load before we load the region nav
          window.addEventListener('milo:modal:loaded', loadRegionNav, { once: true });
        }
      });
      // Set aria-expanded to false when region modal is closed
      window.addEventListener('milo:modal:closed', () => {
        if (isRegionPickerExpanded()) {
          regionPickerElem.setAttribute('aria-expanded', 'false');
        }
      });
    } else {
      // No hash -> region selector expands a dropdown
      regionPickerElem.setAttribute('aria-haspopup', 'true');
      regionPickerElem.href = '#'; // reset href value to not get treated as a fragment
      regionSelector.href = localizeLink(regionSelector.href);
      decorateAutoBlock(regionSelector); // add fragment-specific class(es)
      this.elements.regionPicker.append(regionSelector); // add fragment after regionPickerElem
      const { default: initFragment } = await import('../fragment/fragment.js');
      await initFragment(regionSelector); // load fragment and replace original link
      // Update aria-expanded on click
      regionPickerElem.addEventListener('click', (e) => {
        e.preventDefault();
        const isDialogActive = regionPickerElem.getAttribute('aria-expanded') === 'true';
        regionPickerElem.setAttribute('aria-expanded', !isDialogActive);
      });
      // Close region picker dropdown on outside click
      document.addEventListener('click', (e) => {
        if (isRegionPickerExpanded()
          && !e.target.closest(`.${regionPickerWrapperClass}`)) {
          regionPickerElem.setAttribute('aria-expanded', false);
        }
      });
    }

    return this.elements.regionPicker;
  };

  decorateSocial = () => {
    this.elements.social = '';
    const socialBlock = this.body.querySelector('.social');
    if (!socialBlock) return this.elements.social;

    const socialElem = toFragment`<ul class="feds-social" daa-lh="Social"></ul>`;

    const sanitizeLink = (link) => link.replace('#_blank', '').replace('#_dnb', '');

    CONFIG.socialPlatforms.forEach((platform, index) => {
      const link = socialBlock.querySelector(`a[href*="${platform}"]`);
      if (!link) return;

      const iconElem = toFragment`<li class="feds-social-item">
          <a
            href="${sanitizeLink(link.href)}"
            class="feds-social-link"
            aria-label="${platform}"
            daa-ll="${getAnalyticsValue(platform, index + 1)}"
            target="_blank">
            <svg aria-hidden="true" xmlns="http://www.w3.org/2000/svg" class="feds-social-icon">
              <use href="#footer-icon-${platform}" />
            </svg>
          </a>
        </li>`;

      socialElem.append(iconElem);
    });

    this.elements.social = socialElem.childElementCount !== 0 ? socialElem : '';

    return this.elements.social;
  };

  decoratePrivacy = () => {
    this.elements.legal = '';
    // Get the legal links wrapper by looking for the copyright text's parent
    const copyrightElem = this.body.querySelector('div > p > em');
    if (!copyrightElem) return this.elements.legal;

    const privacyContent = copyrightElem.closest('div');

    // Decorate copyright element
    const currentYear = new Date().getFullYear();
    copyrightElem.remove();

    // Add Ad Choices icon
    const adChoicesElem = privacyContent.querySelector('a[href*="#interest-based-ads"]');
    adChoicesElem?.prepend(toFragment`<svg aria-hidden="true" xmlns="http://www.w3.org/2000/svg" class="feds-adChoices-icon" focusable="false">
        <use href="#footer-icon-adchoices" />
      </svg>`);

    this.elements.legal = toFragment`<div class="feds-footer-legalWrapper" daa-lh="Legal"></div>`;
    const linkDivider = '<span class="feds-footer-privacyLink-divider" aria-hidden="true">/</span>';

    let privacyContentIndex = 0;
    while (privacyContent.children.length) {
      const privacySection = privacyContent.firstElementChild;
      privacySection.classList.add('feds-footer-privacySection');
      privacySection.querySelectorAll('a').forEach((link, index) => {
        link.classList.add('feds-footer-privacyLink');
        link.setAttribute('daa-ll', getAnalyticsValue(link.textContent, index + 1));
        const privacySectionListItem = document.createElement('li');
        privacySectionListItem.classList.add('feds-footer-privacy-listitem');
        link.parentNode.insertBefore(privacySectionListItem, link);
        privacySectionListItem.appendChild(link);
        if (index !== privacySection.querySelectorAll('a').length - 1) {
          privacySectionListItem.innerHTML += linkDivider;
        }
      });
      this.elements.legal.append(privacySection);

      if (privacyContentIndex === 0) {
        const privacySectionList = document.createElement('ul');
        [...privacySection.attributes].forEach((attr) => {
          privacySectionList.setAttribute(attr.name, attr.value);
        });
        const copyrightListItem = document.createElement('li');
        copyrightListItem.classList.add('feds-footer-privacy-listitem');
        copyrightListItem.innerHTML = linkDivider;
        copyrightListItem.prepend(copyrightElem);
        copyrightElem.replaceWith(toFragment`<span class="feds-footer-copyright">Copyright © ${currentYear} ${copyrightElem.textContent}</span>`);
        privacySectionList.prepend(copyrightListItem);
        privacySectionList.innerHTML += privacySection.innerHTML.replace(/( \/ )/g, '');
        privacySection.parentNode.replaceChild(privacySectionList, privacySection);
      }
      privacyContentIndex += 1;
    }

    return this.elements.legal;
  };

  decorateFooter = () => {
    this.elements.footer = toFragment`<div class="feds-footer-wrapper">
        ${this.elements.footerMenu}
        ${this.elements.featuredProducts}
        <div class="feds-footer-options">
          <div class="feds-footer-miscLinks">
            ${this.elements.regionPicker}
            ${this.elements.social}
          </div>
          ${this.elements.legal}
        </div>
      </div>`;

    return this.elements.footer;
  };
}

export default function init(block) {
  try {
    const footer = new Footer({ block });
    if (isDarkMode()) block.classList.add('feds--dark');
    return footer;
  } catch (e) {
    lanaLog({ message: 'Could not create footer', e });
    return null;
  }
}

