      window.adobePrivacy?.showPreferenceCenter();
    }
  });
};

export const loadGoogleLogin = async (getMetadata, loadIms, loadScript, getConfig) => {
  const googleLogin = getMetadata('google-login')?.toLowerCase();
  if (window.adobeIMS?.isSignedInUser() || !['mobile', 'desktop', 'on'].includes(googleLogin)) return;
  const desktopViewport = window.matchMedia('(min-width: 900px)').matches;
  if (googleLogin === 'mobile' && desktopViewport) return;
  if (googleLogin === 'desktop' && !desktopViewport) return;

  const { default: initGoogleLogin } = await import('../features/google-login.js');
  initGoogleLogin(loadIms, getMetadata, loadScript, getConfig);
};

/**
 * Executes everything that happens a lot later, without impacting the user experience.
 */
const loadDelayed = ([
  getConfig,
  getMetadata,
  loadScript,
  loadStyle,
  loadIms,
], DELAY = 3000) => new Promise((resolve) => {
  setTimeout(() => {
    loadPrivacy(getConfig, loadScript);
    loadJarvisChat(getConfig, getMetadata, loadScript, loadStyle);
    loadGoogleLogin(getMetadata, loadIms, loadScript, getConfig);
    if (getMetadata('interlinks') === 'on') {
      const { locale } = getConfig();
      const path = `${locale.contentRoot}/keywords.json`;
      const language = locale.ietf?.split('-')[0];
      import('../features/interlinks.js').then((mod) => { mod.default(path, language); resolve(mod); });
    } else {
      resolve(null);
    }
    import('../utils/samplerum.js').then(({ sampleRUM }) => sampleRUM());
  }, DELAY);
});

export default loadDelayed;

export const PROD_BASE_URL = 'https://www.adobe.com/seotech/api';

export const REGEX_ADOBETV = /(?:https?:\/\/)?(?:stage-)?video.tv.adobe.com\/v\/([\d]+)/;
export const REGEX_YOUTUBE = /(?:https?:\/\/)?(?:www\.)?(?:youtube\.com|youtu\.be)\/(?:watch\?v=)?([a-zA-Z0-9_-]+)/;
export const VIDEO_OBJECT_PROVIDERS = [
  { provider: 'adobe', regex: REGEX_ADOBETV },
  { provider: 'youtube', regex: REGEX_YOUTUBE },
];

export function logError(msg) {
  window.lana?.log(`SEOTECH: ${msg}`, {
    debug: false,
    implicitSampleRate: 100,
    sampleRate: 100,
    tags: 'errorType=seotech',
  });
}

export function parseVideoUrl(url, providers = VIDEO_OBJECT_PROVIDERS) {
  for (const { regex, provider } of providers) {
    const match = url.match(regex);
    if (match) {
      return { provider, id: match[1] };
    }
  }
  return null;
}

export async function getVideoObject(url, { baseUrl = PROD_BASE_URL } = {}) {
  const parsedUrl = parseVideoUrl(url);
  if (!parsedUrl) {
    throw new Error(`Invalid video url: ${url}`);
  }
  const { provider, id } = parsedUrl;
  const videoObjectUrl = `${baseUrl}/json-ld/types/video-object/providers/${provider}/${id}`;
  const resp = await fetch(videoObjectUrl, { headers: { 'Content-Type': 'application/json' } });
  const body = await resp?.json();
  if (!resp.ok) {
    throw new Error(`Failed to fetch video: ${body?.error}`);
  }
  return body.videoObject;
}

// https://github.com/orgs/adobecom/discussions/2633
export function getRepoByImsClientId(imsClientId) {
  return {
    'adobedotcom-cc': 'cc',
    acrobatmilo: 'dc',
    bacom: 'bacom',
    homepage_milo: 'homepage',
    milo: 'milo',
  }[imsClientId];
}

export async function sha256(message) {
  const msgUint8 = new TextEncoder().encode(message);
  const hashBuffer = await window.crypto.subtle.digest('SHA-256', msgUint8);
  const hashArray = Array.from(new Uint8Array(hashBuffer));
  const hashHex = hashArray.map((b) => b.toString(16).padStart(2, '0')).join('');
  return hashHex;
}

export async function getStructuredData(bucket, id, { baseUrl = PROD_BASE_URL } = {}) {
  if (!bucket || !id) throw new Error('bucket and id are required');
  const url = `${baseUrl}/structured-data/${bucket}/${id}`;
  const resp = await fetch(url);
  if (!resp || !resp.ok) return null;
  const body = await resp.json();
  return body;
}

export async function appendScriptTag({ locationUrl, getMetadata, createTag, getConfig }) {
  const url = new URL(locationUrl);
  const params = new URLSearchParams(url.search);
  const baseUrl = params.get('seotech-api-base-url') || undefined;
  const append = (obj, className) => {
    if (!obj) return;
    const attributes = { type: 'application/ld+json' };
    if (className) attributes.class = className;
    const script = createTag('script', attributes, JSON.stringify(obj));
    document.head.append(script);
  };

  const promises = [];
  if (getMetadata('seotech-structured-data') === 'on') {
    const bucket = getRepoByImsClientId(getConfig()?.imsClientId);
    const id = await sha256(url.pathname?.replace('.html', ''));
    promises.push(getStructuredData(bucket, id, { baseUrl })
      .then((obj) => append(obj, 'seotech-structured-data'))
      .catch((e) => logError(e.message)));
  }
  const videoUrl = getMetadata('seotech-video-url');
  if (videoUrl) {
    promises.push(getVideoObject(videoUrl, { baseUrl })
      .then((videoObject) => append(videoObject, 'seotech-video-url'))
      .catch((e) => logError(e.message)));
  }
  return Promise.all(promises);
}

export default appendScriptTag;

export default function lanaLogging() {
  const fricPage = document.querySelector('meta[name="dc-widget-version"]');
  const lanaOptions = { sampleRate: 1 };

  const lanaCspOptions = { sampleRate: 0.001 };

  // DC Converter Logging
  window.addEventListener('DC_Hosted:Ready', () => {
    const LANA_DC_WIDGET = document.querySelectorAll('[class*=ErrorDisplay]');
    LANA_DC_WIDGET.forEach((lanaDcw) => {
      if (fricPage && lanaDcw.querySelector('[class*=ErrorDisplay]')) {
        lanaOptions.tags = 'DC_Milo,Frictionless';
        window.lana?.log('DC Widget Failed ¶ Reason: Error', lanaOptions);
      }
    });
  });

  setTimeout(() => {
    if (fricPage && !window.dc_hosted) {
      lanaOptions.tags = 'DC_Milo,Frictionless';
      window.lana?.log('DC Widget Didn\'t Load ¶ Reason: DC Hosted did not load', lanaOptions);
    }
  }, 10000);

  window.dcwErrors?.forEach((error) => {
    lanaOptions.tags = 'DC_Milo,Frictionless';
    window.lana?.log(error, lanaOptions);
  });

  // Content Security Policy Logging
  if (fricPage) {
    window.cspErrors?.forEach((error) => {
      lanaCspOptions.tags = 'DC_Milo,Frictionless,CSP';
      window.lana?.log(error, lanaCspOptions);
    });
    document.addEventListener('securitypolicyviolation', (e) => {
      lanaCspOptions.tags = 'DC_Milo,Frictionless,CSP';
      window.lana?.log(`${e.violatedDirective} violation ¶ Refused to load content from ${e.blockedURI}`, lanaCspOptions);
    });
  }
}

