  .feds-menu-column--group .feds-menu-column:not(:last-child) {
    margin-bottom: 0;
    padding-bottom: 0;
    border-bottom: none;
  }

  .feds-menu-section + .feds-menu-section {
    margin-top: 12px;
  }

  .feds-menu-headline {
