selectors.profileDropdown = `
  ${selectors.signInDropdown} a[href],
  ${selectors.signInDropdown} button:not([disabled]),
  ${selectors.profileMenu} a[href],
  ${selectors.profileMenu} button:not([disabled])
`;

selectors.popupItems = `
  ${selectors.navLink},
  ${selectors.promoLink},
  ${selectors.imagePromo},
  ${selectors.cta},
  ${selectors.regionPicker},
  ${selectors.socialLink},
  ${selectors.privacyLink}
`;

// This method covers focusable elements only, so we aren’t interested in SVGs for example.
const isElementVisible = (elem) => !!(
  elem
    && elem instanceof HTMLElement
    && (elem.offsetWidth && elem.offsetHeight)
    && window.getComputedStyle(elem).getPropertyValue('visibility') !== 'hidden'
);

const getNextVisibleItemPosition = (position, items) => {
  for (let newPosition = position + 1; newPosition < items.length; newPosition += 1) {
    if (isElementVisible(items[newPosition])) return newPosition;
  }
  return -1;
};

const getPreviousVisibleItemPosition = (position, items) => {
  for (let newPosition = position - 1; newPosition >= 0; newPosition -= 1) {
    if (isElementVisible(items[newPosition])) return newPosition;
  }
  return -1;
};

const getOpenPopup = () => document.querySelector(selectors.expandedPopupTrigger)
  ?.parentElement.querySelector(selectors.popup);

export {
  isElementVisible,
  getNextVisibleItemPosition,
  getPreviousVisibleItemPosition,
  selectors,
  getOpenPopup,
};

/* eslint-disable class-methods-use-this */
import { selectors, getNextVisibleItemPosition, getPreviousVisibleItemPosition } from './utils.js';
import Popup from './popup.js';
import MobilePopup from './mobilePopup.js';
import { closeAllDropdowns, trigger, logErrorFor } from '../utilities.js';

class MainNavItem {
  constructor() {
    this.desktop = window.matchMedia('(min-width: 900px)');
    this.popup = new Popup({ mainNav: this });
    this.mobilePopup = new MobilePopup({ mainNav: this });
    this.addEventListeners();
  }

  addEventListeners() {
    document.querySelector(selectors.globalNav)
      .addEventListener('keydown', (e) => logErrorFor(() => {
        if (!e.target.closest(selectors.fedsNav) || e.target.closest(selectors.popup)) {
          return;
        }

        const newNav = !!document.querySelector('header.new-nav');

        switch (e.code) {
          case 'Tab': {
            if (newNav) {
              const activePopup = document.querySelector(selectors.activePopup);
              if (!activePopup) e.preventDefault();
              const items = [...document.querySelectorAll(`${selectors.mainMenuItems}, ${selectors.mainMenuLinks}, ${selectors.mainNavToggle}`)];
              const current = items.findIndex((x) => x === e.target);
              if (current > -1) {
                const next = current < items.length - 1 ? current + 1 : 0;
                const prev = current > 0 ? current - 1 : items.length - 1;
                if (e.shiftKey) items[prev].focus();
                else items[next].focus();
              } else items?.[0]?.focus();
              break;
            }

            if (e.shiftKey) {
              const { prev, openTrigger } = this.getState();
              if (openTrigger) {
                if (prev === -1) {
                  closeAllDropdowns();
                } else {
                  e.preventDefault();
                  this.focusPrev({ focus: 'last' });
                }
              }
            }
            break;
          }
          case 'Escape': {
            closeAllDropdowns();
            const activePopup = document.querySelector(selectors.activePopup);
            if (newNav && !activePopup) {
              const toggle = document.querySelector('header.new-nav .feds-toggle');
              toggle?.click();
              toggle?.focus();
            }
            break;
          }
          case 'ArrowLeft': {
            if (newNav) break;
            const { next, prev } = this.getState();
            if (document.dir !== 'rtl') {
              if (prev === -1) break;
              this.focusPrev({ focus: null });
            } else {
              if (next === -1) break;
              this.focusNext({ focus: null });
            }
            break;
          }
          case 'ArrowUp': {
            if (newNav) break;
            e.preventDefault();
            e.stopPropagation();
            this.focusPrev({ focus: 'last' });
            break;
          }
          case 'ArrowRight': {
            if (newNav) break;
            const { next, prev, openTrigger } = this.getState();
            if (document.dir !== 'rtl') {
              if (next === -1) break;
              this.focusNext();
            } else {
              if (prev === -1) break;
              this.focusPrev({ focus: null });
            }
            if (openTrigger) {
              this.open();
            }
            break;
          }
          case 'ArrowDown': {
            if (newNav) break;
            e.stopPropagation();
            e.preventDefault();
            const { items, curr } = this.getState();
            if (items[curr] && items[curr].hasAttribute('aria-haspopup')) {
              this.open({ focus: 'first' });
              return;
            }
            this.focusNext();
            break;
          }
          default:
            break;
        }
      }, `mainNav key failed ${e.code}`, 'gnav-keyboard', 'error'));
  }

  getState = () => {
    const items = [...document.querySelectorAll(selectors.mainNavItems)];
    const openTrigger = document.querySelector(selectors.expandedPopupTrigger);
    const currentEl = document.activeElement
      .closest(selectors.navItem)
      ?.querySelector(selectors.mainNavItems);
    const curr = items.findIndex((el) => el === currentEl);
    return {
      items,
      curr,
      prev: getPreviousVisibleItemPosition(curr, items),
      next: getNextVisibleItemPosition(curr, items),
      openTrigger,
    };
  };

  focusCurr = () => {
    const { items, curr } = this.getState();
    items[curr].focus();
  };

  focusPrev = ({ focus } = {}) => {
    const { items, prev } = this.getState();
    const open = document.querySelector(selectors.expandedPopupTrigger);
    closeAllDropdowns();
    if (prev === -1) return;
    items[prev].focus();
    if (open) {
      this.open({ focus });
    }
  };

  focusNext = () => {
    const { items, next } = this.getState();
    if (next === -1) return;
    closeAllDropdowns();
    items[next].focus();
  };

  open = ({ focus, triggerEl, e } = {}) => {
    const { items, curr } = this.getState();
    const triggerElement = triggerEl || items[curr];
    if (!triggerElement || !triggerElement.hasAttribute('aria-haspopup')) return;
    if (e) e.preventDefault();
    if (triggerElement.getAttribute('aria-expanded') === 'false') {
      trigger({ element: triggerElement });
    }
    const navItem = triggerElement.parentElement;
    const popupEl = navItem.querySelector(selectors.popup);
    if (popupEl) {
      if (this.desktop.matches) {
        this.popup.open({ focus });
      } else {
        this.mobilePopup.open({ focus });
      }
      return;
    }

    // We need to wait for the popup to be added to the DOM before we can open it.
    const observer = new MutationObserver(() => {
      observer.disconnect();
      if (this.desktop.matches) {
        this.popup.open({ focus });
      } else {
        this.mobilePopup.open({ focus });
      }
    });
    observer.observe(navItem, { childList: true });
  };
}

export default MainNavItem;

/* eslint-disable class-methods-use-this */
import {
  getNextVisibleItemPosition,
  getPreviousVisibleItemPosition,
  isElementVisible,
  getOpenPopup,
  selectors,
} from './utils.js';
import { closeAllDropdowns, dropWhile, logErrorFor, setActiveDropdown, takeWhile } from '../utilities.js';

const closeHeadlines = () => {
  const open = [...document.querySelectorAll(`${selectors.headline}[aria-expanded="true"]`)];
  open.forEach((el) => el.setAttribute('aria-expanded', 'false'));
  // Shift active class back to the parent of the first closed headline
  setActiveDropdown(open[0]);
};

const openHeadline = ({ headline, focus } = {}) => {
  closeHeadlines();
  if (headline.getAttribute('aria-haspopup') === 'true') {
    headline.setAttribute('aria-expanded', 'true');
    setActiveDropdown(headline);
    const section = headline.closest(selectors.section)
      || headline.closest(selectors.column);
    const items = [...section.querySelectorAll(selectors.popupItems)]
      .filter((el) => isElementVisible(el));
    if (focus === 'first') items[0].focus();
    if (focus === 'last') items[items.length - 1].focus();
  }
};

const getState = (element = getOpenPopup()) => {
  if (!element) return { popupItems: [] };
  const popupItems = [...element.querySelectorAll(selectors.popupItems)];
  // In the markup either a section OR column can contain a expandable headline
  // which is what we are interested in - so we can treat them both as sections.
  const section = document.activeElement.closest(selectors.section)
    || document.activeElement.closest(selectors.column);
  let allSections = [...element.querySelectorAll(selectors.section)];
  if (!allSections.length) allSections = [...element.querySelectorAll(selectors.column)];
  const visibleSections = allSections.filter((el) => isElementVisible(el));
  const currentSection = visibleSections.findIndex((node) => node.isEqualNode(section));
  const firstHeadline = visibleSections[0]?.querySelector(selectors.headline);
  const lastHeadline = visibleSections[visibleSections.length - 1]
    ?.querySelector(selectors.headline);
  const prevHeadline = visibleSections[currentSection - 1]
    ?.querySelector(selectors.headline);
  const nextHeadline = visibleSections[currentSection + 1]
    ?.querySelector(selectors.headline);
  return {
    visibleSections,
    currentSection,
    firstHeadline,
    lastHeadline,
    prevHeadline,
    nextHeadline,
    popupItems,
  };
};

class Popup {
  constructor({ mainNav }) {
    this.mainNav = mainNav;
    this.addEventListeners();
    this.desktop = window.matchMedia('(min-width: 900px)');
  }

  open({ focus } = {}) {
    const { firstHeadline, lastHeadline, popupItems } = getState();
    if (!popupItems.length) return;

    const headline = focus === 'last' ? lastHeadline : firstHeadline;

    if (headline && headline.getAttribute('aria-haspopup') === 'true') {
      closeHeadlines();
      headline.setAttribute('aria-expanded', 'true');
    }

    const first = getNextVisibleItemPosition(-1, popupItems);
    const last = getPreviousVisibleItemPosition(popupItems.length, popupItems);
    if (focus === 'first') popupItems[first].focus();
    if (focus === 'last') popupItems[last].focus();
    setActiveDropdown(focus === 'first' ? popupItems[first] : popupItems[last]);
  }

  mobileArrowUp = ({ prev, curr, element, isFooter, newNav }) => {
    // Case 1:  Move focus to the previous item
    const state = getState(element);
    const { currentSection } = state;
    const popupItems = newNav && !isFooter ? this.popupItems() : state.popupItems;
    if (prev !== -1 && curr - 1 === prev) {
      popupItems[prev].focus();
      if (currentSection !== getState(element).currentSection) closeHeadlines();
      return;
    }

    // Case 2: No headline + no previous item, move to the main nav
    const { prevHeadline } = getState(element);
    if (!prevHeadline && !newNav) {
      this.focusMainNav(isFooter);
      return;
    }

    // Case 3: Open the previous headline
    if (newNav && !isFooter) popupItems?.[popupItems.length - 1]?.focus();
    else openHeadline({ headline: prevHeadline, focus: 'last' });
  };

  mobileArrowDown = ({ next, element, isFooter, newNav }) => {
    // Case 1: Move focus to the next item
    const state = getState(element);
    const { currentSection } = state;
    const popupItems = newNav && !isFooter ? this.popupItems() : state.popupItems;
    if (next !== -1) {
      popupItems[next].focus();
      if (currentSection !== getState(element).currentSection) closeHeadlines();
      return;
    }
    // Case 2: No headline + no next item, move to the main nav
    const { nextHeadline } = getState(element);
    if (!nextHeadline && !newNav) {
      closeHeadlines();
      this.focusMainNavNext(isFooter);
      return;
    }

    // Case 3: Open the next headline
    if (newNav && !isFooter) popupItems?.[0]?.focus();
    else openHeadline({ headline: nextHeadline, focus: 'first' });
  };

  focusMainNav = (isFooter) => {
    if (isFooter) return;
    this.mainNav.focusCurr();
  };

  focusMainNavNext = (isFooter) => {
    if (isFooter) return;
    this.mainNav.focusNext();
    this.mainNav.open();
  };

  popupItems = () => {
    const activePopup = document.querySelector(selectors.activePopup);
    if (!activePopup) return [];
    const tabs = [...activePopup.querySelectorAll(selectors.tab)];
    const activeTab = tabs.find((tab) => tab.getAttribute('aria-selected') === 'true');
    const anteActiveTab = takeWhile(tabs, (tab) => tab !== activeTab);
    const postActiveTab = dropWhile(tabs, (tab) => tab !== activeTab).slice(1);
    const activeLinks = [...activePopup.querySelectorAll(selectors.activeLinks)];
    const stickyCTA = activePopup.querySelector(selectors.stickyCta);
    const topBarLinks = activePopup.querySelectorAll(selectors.topBarLinks);
    const breadcrumbLinks = activePopup.querySelectorAll(selectors.breadCrumbItems);
    return [
      ...anteActiveTab,
      activeTab,
      ...activeLinks,
      stickyCTA,
      ...postActiveTab,
      ...topBarLinks,
      ...breadcrumbLinks,
    ].filter(Boolean);
  };

  handleKeyDown = ({ e, element, isFooter }) => {
    const newNav = !!document.querySelector('header.new-nav');
    const isLocalNav = !!document.querySelector('header.local-nav');
    const popupItems = newNav && !isFooter
      ? this.popupItems()
      : [...element.querySelectorAll(selectors.popupItems)];
    const curr = popupItems.findIndex((el) => el === e.target);
    const prev = getPreviousVisibleItemPosition(curr, popupItems);
    const next = getNextVisibleItemPosition(curr, popupItems);

    e.preventDefault();
    e.stopPropagation();

    switch (e.code) {
      case 'Tab': {
        if (e.shiftKey) {
          this.mobileArrowUp({ prev, curr, element, isFooter, newNav });
        } else {
          this.mobileArrowDown({ curr, next, element, isFooter, newNav });
        }
        break;
      }
      case 'Escape': {
        closeAllDropdowns();
        this.focusMainNav(isFooter);
        if (newNav && isLocalNav && !isFooter) {
          const toggle = document.querySelector('header.new-nav .feds-toggle');
          toggle?.click();
          toggle?.focus();
        }
        break;
      }
      case 'ArrowLeft': {
        if (newNav) break;
        const { prevHeadline, nextHeadline } = getState(element);
        const headline = document.dir !== 'rtl' ? prevHeadline : nextHeadline;
        if (!headline) {
          if (!isFooter) closeHeadlines();
          if (document.dir !== 'rtl') {
            this.focusMainNav(isFooter);
          } else {
            this.focusMainNavNext(isFooter);
          }
          break;
        }
        openHeadline({ headline, focus: 'first' });
        break;
      }
      case 'ArrowUp': {
        if (newNav) break;
        this.mobileArrowUp({ prev, curr, element, isFooter });
        break;
      }
      case 'ArrowRight': {
        if (newNav) break;
        const { prevHeadline, nextHeadline } = getState(element);
        const headline = document.dir !== 'rtl' ? nextHeadline : prevHeadline;
        if (!headline) {
          if (!isFooter) closeHeadlines();
          if (document.dir !== 'rtl') {
            this.focusMainNavNext(isFooter);
          } else {
            this.focusMainNav(isFooter);
          }
          break;
        }
        openHeadline({ headline, focus: 'first' });
        break;
      }
      case 'ArrowDown': {
        if (newNav) break;
        this.mobileArrowDown({ next, element, isFooter });
        break;
      }
      default:
        break;
    }
  };

  addEventListeners = () => {
    document.querySelector(selectors.globalNav)
      ?.addEventListener('keydown', (e) => logErrorFor(() => {
        const element = getOpenPopup();
        if (!e.target.closest(selectors.popup) || !element || this.desktop.matches) return;
        this.handleKeyDown({ e, element, popupEl: element, isFooter: false });
      }, `popup key failed ${e.code}`, 'gnav-keyboard', 'error'));

    document.querySelector(selectors.globalFooter)
      ?.addEventListener('keydown', (e) => logErrorFor(() => {
        const element = e.target.closest(selectors.menuContent);
        if (!element || this.desktop.matches) return;

        const firstNavLink = element.querySelector(selectors.popupItems);
        const firstHeadline = element.querySelector(selectors.headline);
        const isFirstNavlink = e.target === firstNavLink;
        const isFirstHeadline = e.target === firstHeadline;
        const shiftTabOutOfFooter = e.shiftKey && (isFirstNavlink || isFirstHeadline);
        if (shiftTabOutOfFooter) return;

        // special case, the first dropdown needs some special logic to allow opening.
        if (e.target.closest(selectors.headline)) {
          openHeadline({ headline: e.target.closest(selectors.headline), focus: 'first' });
          e.preventDefault();
          e.stopPropagation();
          return;
        }

        this.handleKeyDown({
          e,
          element,
          isFooter: true,
        });
      }, `footer key failed ${e.code}`, 'gnav-keyboard', 'error'));
  };
}

export default Popup;

/* eslint-disable class-methods-use-this */
import {
  getNextVisibleItemPosition,
  getPreviousVisibleItemPosition,
  getOpenPopup,
  selectors,
} from './utils.js';
import { closeAllDropdowns, logErrorFor } from '../utilities.js';

const getState = ({ e, element } = {}) => {
  if (!element) return {};
  const popupItems = [...element.querySelectorAll(selectors.popupItems)];
  const curr = popupItems.findIndex((el) => el === e.target);
  const column = document.activeElement.closest(selectors.column);
  const visibleColumns = [...element.querySelectorAll(selectors.column)];
  const currentColumn = visibleColumns.findIndex((node) => node.isEqualNode(column));

  return {
    popupItems,
    curr,
    prev: getPreviousVisibleItemPosition(curr, popupItems),
    next: getNextVisibleItemPosition(curr, popupItems),
    prevColumn: visibleColumns[currentColumn - 1],
    nextColumn: visibleColumns[currentColumn + 1],
  };
};

class Popup {
  constructor({ mainNav }) {
    this.mainNav = mainNav;
    this.addEventListeners();
    this.desktop = window.matchMedia('(min-width: 900px)');
  }

  open({ focus } = {}) {
    const popupItems = [...(getOpenPopup()?.querySelectorAll(selectors.popupItems) || [])];
    if (!popupItems.length) return;
    const first = getNextVisibleItemPosition(-1, popupItems);
    const last = getPreviousVisibleItemPosition(popupItems.length, popupItems);
    if (focus === 'first') popupItems[first].focus();
    if (focus === 'last') popupItems[last].focus();
  }

  focusMainNav = (isFooter) => {
    if (isFooter) return; // There is no main nav for the footer, so we can ignore this
    this.mainNav.focusCurr();
  };

  focusMainNavNext = (isFooter) => {
    if (isFooter) return;
    const { next } = this.mainNav.getState();
    if (next >= 0) {
      this.mainNav.focusNext();
      this.mainNav.open();
      return;
    }

    // On the last main nav item, close all dropdowns and focus current main nav item
    closeAllDropdowns();
    this.mainNav.focusCurr();
  };

  handleKeyDown = ({ e, element, isFooter }) => {
    const { popupItems, prev, next, prevColumn, nextColumn } = getState({ e, element });

    const shiftTabOutOfFooter = isFooter && e.code === 'Tab' && e.shiftKey && prev === -1;
    const tabOutOfFooter = isFooter && e.code === 'Tab' && !e.shiftKey && next === -1;

    if (tabOutOfFooter || shiftTabOutOfFooter) return;

    e.preventDefault();
    e.stopPropagation();

    switch (e.code) {
      case 'Tab': {
        if (e.shiftKey) {
          if (prev === -1) {
            this.focusMainNav(isFooter);
            break;
          }
          popupItems[prev].focus();
        } else {
          if (next === -1) {
            this.focusMainNavNext(isFooter);
            break;
          }
          popupItems[next].focus();
        }

        break;
      }
      case 'Escape': {
        closeAllDropdowns();
        this.focusMainNav(isFooter);
        break;
      }
      case 'ArrowLeft': {
        const noPrev = document.dir !== 'rtl' && !prevColumn?.querySelector(selectors.popupItems);
        const noNext = document.dir === 'rtl' && !nextColumn?.querySelector(selectors.popupItems);
        if (noPrev || noNext) {
          this.focusMainNav(isFooter);
          break;
        }
        if (document.dir !== 'rtl') {
          prevColumn.querySelector(selectors.popupItems).focus();
        } else {
          nextColumn.querySelector(selectors.popupItems).focus();
        }
        break;
      }
      case 'ArrowUp': {
        if (prev === -1) {
          this.focusMainNav(isFooter);
          break;
        }
        popupItems[prev].focus();
        break;
      }
      case 'ArrowRight': {
        const noNext = document.dir !== 'rtl' && !nextColumn?.querySelector(selectors.popupItems);
        const noPrev = document.dir === 'rtl' && !prevColumn?.querySelector(selectors.popupItems);
        if (noNext || noPrev) {
          this.focusMainNav(isFooter);
          break;
        }
        if (document.dir !== 'rtl') {
          nextColumn.querySelector(selectors.popupItems).focus();
        } else {
          prevColumn.querySelector(selectors.popupItems).focus();
        }
        break;
      }
      case 'ArrowDown': {
        if (next === -1) {
          this.focusMainNavNext(isFooter);
          break;
        }
        popupItems[next].focus();
        break;
      }
      default:
        break;
    }
  };

  addEventListeners = () => {
    document.querySelector(selectors.globalNav)
      ?.addEventListener('keydown', (e) => logErrorFor(() => {
        const element = getOpenPopup();
        if (!e.target.closest(selectors.popup) || !element || !this.desktop.matches) return;
        this.handleKeyDown({ e, element, isFooter: false });
      }, `popup key failed ${e.code}`, 'gnav-keyboard', 'error'));

    document.querySelector(selectors.globalFooter)
      ?.addEventListener('keydown', (e) => logErrorFor(() => {
        if (!this.desktop.matches) return;
        const element = e.target.closest(selectors.globalFooter);
        this.handleKeyDown({ e, element, isFooter: true });
      }, `footer key failed ${e.code}`, 'gnav-keyboard', 'error'));
  };
}

export default Popup;

import { selectors } from './utils.js';
import { trigger } from '../utilities.js';

class LocalNavItem {
  constructor() {
    this.localNav = document.querySelector(selectors.localNav);
    this.localNavTrigger = this.localNav?.querySelector(selectors.localNavTitle);
    this.exitLink = this.localNav?.querySelector(selectors.localNavExit);
    this.addEventListeners();
  }

  handleKeyDown = (e) => {
    const { code, target } = e;
    const isHeadline = target.classList.contains(selectors.headline.slice(1));
    switch (code) {
      case 'Space':
      case 'Enter':
        if (isHeadline) {
          e.preventDefault(); // Prevent default scrolling behavior for Space key
          trigger({ element: target, event: e, type: 'headline' });
        }
        break;
      case 'Escape': // close on escape
        e.preventDefault();
        if (this.localNav.classList.contains(selectors.localNavActive.slice(1))) {
          this.localNavTrigger?.click();
          this.localNavTrigger?.focus();
        }
        break;
      default:
        break;
    }
  };

  addEventListeners = () => {
    this.localNav?.addEventListener('keydown', this.handleKeyDown);
    this.exitLink?.addEventListener('focus', (e) => {
      e.preventDefault();
      this.localNavTrigger?.focus();
    });
  };
}

export default LocalNavItem;
